import './App.scss';
import Header from "./components/header";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import Slider from "react-slick";
import CoffeItem from "./components/coffeItem";
import Coffe1 from "./components/img/coffe-1.png";
import CoffeList from "./components/navs/coffeList";
import { useRef, useState, useEffect } from "react";


const App = () => {

  const sliderRef1 = useRef(null);
  const sliderRef2 = useRef(null);

  const [slider1, setSlider1] = useState(null);
  const [slider2, setSlider2] = useState(null);

  const settings = {
    infinite: true,
    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false,
    autoplaySpeed: 3500,
    arrows: false,
    dots: false,

  };

  const coffe = {

    img: Coffe1,
    title: "starbucks",
    info: "melhor preço imperdivel",
    desc: ["melhor produto", "zero açucar", "mais proteinas"],
    preco: "8.00"

  }

  function clickCoffeList(v) {

    sliderRef1.current.slickGoTo(v);

  }

  useEffect(() => {

    setSlider1(sliderRef1.current);
    setSlider2(sliderRef2.current);
  }, []);


  return (
    <div className="App">
      <Header></Header>

      <div className="container">
        <section className="coffe-section">
          <Slider {...settings} ref={sliderRef1} asNavFor={slider2}>

            <div >
              <CoffeItem {...coffe} />

            </div>
            <div>

              <CoffeItem {...coffe} />

            </div>
            <div>

              <CoffeItem {...coffe} />


            </div>

          </Slider>

          <div className="coffe-list-container">

            <Slider
              className="slider-navbar"
              slidesToShow={3}
              swipeToSlide={true}
              focusOnSelect={true}
              ref={sliderRef2}
              asNavFor={slider1}
              variableWidth = {true}
              >

              <figure className="slider-coffe" style={{width: 50}}><img src={Coffe1} /></figure>
              <figure className="slider-coffe"><img src={Coffe1} /></figure>
              <figure className="slider-coffe"><img src={Coffe1} /></figure>

            </Slider>
          </div>

        </section>
      </div>


    </div>
  );
}

export default App;
