import './App.scss';
import Header from "./components/header";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import Slider from "react-slick";
import CoffeItem from "./components/coffeItem";
import Coffe1 from "./components/img/coffe-1.png";
import CoffeList from "./components/navs/coffeList";
import {useRef,useState} from "react";


const App = () => {

  const sliderRef = useRef(null);

  const settings = {
    dots: true,
    infinite: true,
    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false,
    autoplaySpeed: 3500,
    arrows: false,
    dots: false,

  };

  const coffe = {

    img: Coffe1,
    title: "starbucks",
    info: "melhor preço imperdivel",
    desc: ["melhor produto", "zero açucar", "mais proteinas"],
    preco: "8.00"

  }

  function clickCoffeList (v) {

    sliderRef.current.slickGoTo(v);

  }

  const [teste,setest] = useState(0);


  return (
    <div className="App">
      <Header></Header>

      <div className="container">
        <section className="coffe-section">
            <Slider {...settings} ref={sliderRef} asNavFor={setest(teste + 1)}>

              <div >
                <CoffeItem {...coffe} />

              </div>
              <div>

                <CoffeItem {...coffe} />

              </div>
              <div>

                <CoffeItem {...coffe} />


              </div>

            </Slider>

            <div className="coffe-list-container">

               <CoffeList clickEvent={clickCoffeList}></CoffeList>

            </div>

        </section>
      </div>


    </div>
  );
}

export default App;
