import "./nav.scss";
import { useState,useEffect,useRef } from "react";


const Nav = ({children,mobile}) => {

    const aux = useRef(null);
    const [classe,setClasse] = useState("");

    useEffect(()=>{

        document.addEventListener("click",(e)=>{

            console.log(classe);

            if(!aux.current.contains(e.target) && e.target.classList.contains("ativado")){
                setClasse("");
            }

        });

    },[]);

   const clickMenu = (e) =>{

        setClasse(classe == ""? "ativado": "");

    }

    return(

        <nav className= "nav">

            <ul className={classe} ref={aux}>
                {children.map((v,i)=>{


                    return <li key={i}><a key={i}>{v}</a></li>


                })}
            </ul>

            <div className="menu-nav" onClick={clickMenu}>
                <div></div>
                <div></div>
                <div></div>
            </div>
        </nav>)


}

export default Nav;
